INSERT INTO `categories` (`id`, `name`, `image`, `description`, `type`, `status`) VALUES
(1, 'Bánh bông lan', 'category-1.jpg', 'Danh mục Bánh bông lan tại Ngũ Long Bakery là nơi hội tụ những hương vị mềm mại và tinh tế, làm say lòng mọi thực khách. Được chế biến từ nguyên liệu chất lượng cao, từng chiếc bánh bông lan của chúng tôi mang đến sự hòa quyện hoàn hảo giữa vị béo ngậy của bơ, vị ngọt dịu của đường, và sự mềm mịn đặc trưng trong từng miếng bánh.', 'Sản phẩm', 1),
(2, 'Bánh bò', 'category-2.jpg', 'Danh mục Bánh bò tại Ngũ Long Bakery mang đậm hương vị truyền thống của miền Tây, là lựa chọn lý tưởng để bạn thưởng thức nét độc đáo trong ẩm thực Việt. Những chiếc bánh mềm mại, dai dai với hương vị ngọt thanh, thơm lừng mùi dừa chắc chắn sẽ làm say lòng bất kỳ ai từ lần đầu thưởng thức.', 'Sản phẩm', 1),
(3, 'Bánh trung thu', 'category-3.jpg', 'Danh mục Bánh trung thu tại Ngũ Long Bakery không chỉ là món bánh truyền thống mà còn là biểu tượng của tình thân và sự đoàn viên trong ngày Tết Trung Thu. Với sự kết hợp giữa hương vị cổ điển và sáng tạo, mỗi chiếc bánh là một tuyệt tác, chứa đựng sự tinh tế và tâm huyết của người thợ làm bánh.', 'Sản phẩm', 1),
(4, 'Bánh da lợn', 'category-4.jpg', 'Danh mục Bánh da lợn tại Ngũ Long Bakery mang đậm dấu ấn của ẩm thực truyền thống Việt Nam. Những chiếc bánh được tạo ra từ sự khéo léo và tâm huyết, đem đến hương vị thơm ngon, mềm dẻo và thanh ngọt, gợi nhớ về những ký ức tuổi thơ yên bình.', 'Sản phẩm', 1),
(5, 'Khám phá các loại bánh', NULL, 'Chuyên mục Khám Phá Các Loại Bánh tại Ngũ Long Bakery là nơi bạn có thể khám phá một thế giới bánh ngọt phong phú, đa dạng với các loại bánh nổi bật, được chế biến từ những nguyên liệu tươi ngon và quy trình làm bánh tỉ mỉ. Tại đây, chúng tôi sẽ giới thiệu đến bạn những món bánh đặc trưng, không chỉ mang lại sự hài lòng về hương vị mà còn là niềm tự hào về sự sáng tạo và đam mê của đội ngũ làm bánh tại Ngũ Long Bakery.', 'Tin tức', 1),
(6, 'Bánh mì', 'category-6.jpg', 'Danh mục Bánh mì tại Ngũ Long Bakery mang đến sự kết hợp hoàn hảo giữa hương vị truyền thống và sáng tạo hiện đại, phù hợp với mọi khẩu vị của thực khách. Từ những chiếc bánh mì giòn rụm, mềm mại đến các món ăn kèm hấp dẫn, tất cả đều được làm từ nguyên liệu tươi ngon và quy trình chế biến tinh tế, đảm bảo chất lượng mỗi lần thưởng thức.', 'Sản phẩm', 1),
(7, 'Bánh tráng', 'category-7.jpg', 'Danh mục Bánh tráng tại Ngũ Long Bakery mang đến cho thực khách một trải nghiệm ẩm thực đa dạng, kết hợp giữa hương vị truyền thống và những sáng tạo độc đáo. Với chất lượng nguyên liệu tươi ngon, mỗi chiếc bánh tráng đều được chế biến kỹ lưỡng, giữ được sự mỏng, dai và thơm ngon đặc trưng, tạo nên món ăn vặt hấp dẫn, dễ ăn và đầy dinh dưỡng.', 'Sản phẩm', 1),
(8, 'Rau câu', 'category-8.jpg', 'Danh mục Rau câu tại Ngũ Long Bakery mang đến cho thực khách một lựa chọn món tráng miệng thanh mát, nhẹ nhàng nhưng không kém phần hấp dẫn. Với những món rau câu được làm từ nguyên liệu tự nhiên và quy trình chế biến tỉ mỉ, mỗi miếng rau câu tại Ngũ Long Bakery đều có độ giòn dai hoàn hảo, thấm đẫm hương vị thơm ngon và đẹp mắt.', 'Sản phẩm', 1),
(9, 'Bánh su kem', 'category-9.jpg', 'Danh mục Bánh su kem tại Ngũ Long Bakery mang đến cho bạn những chiếc bánh su thơm ngon, nhẹ nhàng và đầy hấp dẫn, là sự kết hợp hoàn hảo giữa lớp vỏ ngoài giòn tan và lớp nhân kem mịn màng, ngọt ngào. Đây là món tráng miệng lý tưởng cho những ai yêu thích sự ngọt ngào mà không quá ngấy, với mỗi chiếc bánh su kem mang đến cảm giác dễ chịu, thanh mát.', 'Sản phẩm', 1),
(10, 'Xôi xoài', 'category-10.jpg', 'Danh mục Xôi xoài tại Ngũ Long Bakery mang đến một trải nghiệm ẩm thực độc đáo, kết hợp sự ngọt ngào của xoài tươi và sự mềm dẻo của xôi nếp, tạo nên một món ăn nhẹ thơm ngon, dễ dàng chinh phục mọi thực khách. Đây là sự kết hợp tuyệt vời của hương vị ngọt thanh tự nhiên từ xoài và độ dẻo thơm, béo ngậy của xôi, khiến món ăn này trở thành một lựa chọn hoàn hảo cho bữa sáng, bữa tráng miệng hay bất kỳ dịp đặc biệt nào.', 'Sản phẩm', 1),
(11, 'Bánh ít', 'category-11.jpg', 'Danh mục Bánh ít tại Ngũ Long Bakery giới thiệu đến thực khách những chiếc bánh nhỏ xinh, thơm ngon, mang đậm hương vị truyền thống của ẩm thực miền Tây Nam Bộ. Bánh ít là món ăn quen thuộc trong các dịp lễ hội hay những bữa tiệc gia đình, được yêu thích bởi sự mềm dẻo của lớp vỏ bột gạo hòa quyện với nhân thơm ngọt, tạo nên một món ăn vừa đậm đà vừa dễ thưởng thức.', 'Sản phẩm', 1),
(12, 'Bánh crepe', 'category-12.jpg', 'Danh mục Bánh Crepe tại Ngũ Long Bakery mang đến cho thực khách những chiếc bánh crepe mềm mịn, nhẹ nhàng, đầy hương vị và sáng tạo, đáp ứng sở thích của mọi tín đồ yêu thích bánh ngọt. Bánh crepe là món tráng miệng hiện đại, kết hợp hoàn hảo giữa lớp vỏ mỏng tan, nhân đầy đặn, hương vị ngọt ngào và độ tươi ngon của nguyên liệu.', 'Sản phẩm', 1),
(13, 'Hướng dẫn và mẹo chọn bánh', NULL, 'Chuyên mục Hướng Dẫn và Mẹo Chọn Bánh tại Ngũ Long Bakery được tạo ra để giúp bạn không chỉ khám phá mà còn có những quyết định thông minh khi chọn lựa những chiếc bánh yêu thích. Dù bạn là người mới bắt đầu tìm hiểu về các loại bánh, hay một tín đồ của bánh ngọt lâu năm, chuyên mục này sẽ mang đến cho bạn những kiến thức bổ ích để lựa chọn và thưởng thức những chiếc bánh một cách hoàn hảo nhất.', 'Tin tức', 1),
(14, 'Xu hướng và thú vị về bánh', NULL, 'Chuyên mục Xu Hướng và Thú Vị Về Bánh tại Ngũ Long Bakery mang đến cho bạn những cái nhìn mới mẻ và đầy sáng tạo về thế giới bánh. Với những thay đổi không ngừng trong sở thích và thói quen ẩm thực, chúng tôi luôn cập nhật và chia sẻ những xu hướng mới nhất về bánh, cũng như khám phá những điều thú vị, bất ngờ xung quanh từng chiếc bánh mà Ngũ Long Bakery mang đến cho khách hàng. Đây là nơi bạn có thể tìm thấy những ý tưởng sáng tạo, những xu hướng bánh mới và những điều bạn chưa từng nghĩ đến về các món bánh yêu thích.', 'Tin tức', 1);
INSERT INTO `comments` (`id`, `comment`, `status`, `created_at`, `user_id`, `post_id`) VALUES
(1, 'Bài viết rất hấp dẫn! Ngũ Long Bakery đúng là thiên đường của các tín đồ mê bánh. Từ bánh da lợn truyền thống, bánh bông lan trứng muối, đến rau câu sơn thủy, mỗi loại bánh đều mang đậm hương vị đặc trưng riêng, vừa ngon vừa đẹp mắt. Đọc mà chỉ muốn thử hết tất cả! Ai chưa ghé qua Ngũ Long Bakery thì nhất định phải thử một lần, đảm bảo không thất vọng!', 1, '2024-11-30 12:23:55', 1, 1),
(2, 'Bài viết quá hay, đọc mà thấy bánh ngọt không chỉ là món ăn mà còn là nghệ thuật! Sự kết hợp giữa truyền thống và hiện đại như bánh da lợn được biến tấu với hương vị sầu riêng hay bánh bông lan trứng muối là minh chứng rõ ràng cho sức sáng tạo của ẩm thực Việt. Ngũ Long Bakery luôn biết cách làm mới mình nhưng vẫn giữ được hồn cốt truyền thống. Đúng là không ngừng bất ngờ!', 1, '2024-11-30 12:31:21', 1, 2),
(3, 'Bài viết này thực sự làm sống lại cả ký ức tuổi thơ và niềm đam mê khám phá ẩm thực hiện đại! Các loại bánh đặc trưng miền Tây như bánh bò, bánh da lợn tại Ngũ Long Bakery không chỉ giữ nguyên hương vị truyền thống mà còn được biến tấu đầy sáng tạo. Thật tự hào khi thấy văn hóa bánh quê hương được giữ gìn và phát triển một cách tinh tế thế này. Cảm ơn Ngũ Long Bakery vì đã mang nét đẹp ẩm thực miền Tây đến gần hơn với mọi người!', 1, '2024-11-30 12:32:40', 1, 3),
(4, 'Bài viết quá xuất sắc! Đọc xong mới thấy bánh ngọt không chỉ là món ăn mà còn mang ý nghĩa sâu sắc trong văn hóa Việt Nam. Từ chiếc bánh bò, bánh da lợn đến bánh trung thu, mỗi loại bánh đều chứa đựng những câu chuyện, biểu tượng gắn liền với truyền thống và tình cảm gia đình. Ngũ Long Bakery thật tinh tế khi vừa giữ được hồn cốt của bánh Việt, vừa sáng tạo để phù hợp với thời đại. Một bài viết vừa ý nghĩa, vừa truyền cảm hứng!', 1, '2024-11-30 12:33:34', 1, 4),
(5, 'Quá tuyệt vời! Bài viết này như dẫn mình trở lại những ngày xưa, khi mỗi lát bánh da lợn đều là niềm vui giản dị. Ngũ Long Bakery không chỉ tái hiện hương vị truyền thống mà còn chăm chút từng lớp bánh mềm mịn, thơm lừng mùi lá dứa và nước cốt dừa. Đặc biệt, phần chia sẻ cách làm bánh khiến mình càng thêm trân trọng sự tỉ mỉ và tâm huyết trong từng chiếc bánh. Một món quà tuổi thơ được nâng tầm, xứng đáng để thử!', 1, '2024-11-30 12:34:17', 1, 5),
(6, 'Đọc bài viết mà như cảm nhận được mùi thơm béo của khoai môn và sự mềm mịn của từng lớp bánh da lợn! Ngũ Long Bakery thật sự rất sáng tạo khi kết hợp khoai môn vào món bánh truyền thống, tạo nên hương vị độc đáo, vừa quen thuộc lại vừa mới lạ. Đây chắc chắn là món bánh phải thử cho những ai yêu ẩm thực Việt. Cảm ơn bài viết đã giúp mình hiểu thêm về sự tỉ mỉ và tâm huyết của tiệm. Đỉnh cao quá!', 1, '2024-11-30 12:35:03', 1, 6),
(7, 'Bài viết quá hữu ích! Ngũ Long Bakery đúng là nơi mà mỗi dịp đều có một loại bánh phù hợp. Từ bánh bông lan trứng muối đậm đà cho tiệc sinh nhật đến bánh da lợn truyền thống cho những buổi sum họp gia đình, mọi lựa chọn đều tinh tế và ngon miệng. Đặc biệt thích phần chia sẻ bí quyết chọn bánh theo từng dịp, rất chi tiết và dễ áp dụng. Ai còn phân vân chưa biết chọn bánh gì thì bài này là cứu cánh đây!', 1, '2024-11-30 12:35:48', 1, 7),
(8, 'Bài viết thật sự làm mình thèm một ổ bánh mì nóng giòn ngay lập tức! Ngũ Long Bakery đã nâng tầm món bánh mì đường phố với hương vị độc đáo và chất lượng vượt trội. Từ bánh mì heo quay đậm đà, bánh mì chả cá thơm lừng, đến bánh mì ốp la trứng béo ngậy, mỗi món đều mang đậm dấu ấn riêng. Đọc mà cảm nhận được sự tỉ mỉ trong từng nguyên liệu và cách chế biến. Ai yêu ẩm thực đường phố thì đừng bỏ qua trải nghiệm này!', 1, '2024-11-30 12:36:32', 1, 9),
(9, 'Bài viết quá hữu ích! Những mẹo bảo quản bánh ngọt trong bài thật sự rất thiết thực, đặc biệt là với những ai muốn giữ bánh luôn thơm ngon mà không bị khô hay mất vị. Đặc biệt thích phần hướng dẫn phân loại bánh và nhiệt độ bảo quản phù hợp, giúp tránh lãng phí và giữ được hương vị tối ưu. Ngũ Long Bakery không chỉ làm bánh ngon mà còn rất quan tâm đến trải nghiệm của khách hàng. Cảm ơn bài viết đã chia sẻ những thông tin quý giá!', 1, '2024-11-30 12:37:44', 1, 8),
(10, 'Bài viết thực sự cuốn hút! &quot;Những Lưu Ý Khi Bảo Quản Bánh Ngọt Tại Nhà&quot; không chỉ hữu ích mà còn giúp mình hiểu rõ hơn về cách bảo quản bánh sao cho luôn giữ được độ ngon như vừa mua. Còn bài &quot;Sự Phát Triển Của Nghề Làm Bánh Tại Việt Nam Hiện Nay&quot; lại mở ra một góc nhìn đầy tự hào về sự sáng tạo và phát triển vượt bậc của ngành nghề này. Ngũ Long Bakery vừa chia sẻ kiến thức thực tế, vừa truyền cảm hứng về niềm đam mê làm bánh. Đọc xong chỉ muốn thử hết các loại bánh ở đây!', 1, '2024-11-30 12:39:53', 1, 10),
(11, 'Bài viết rất hay và đầy cảm hứng! Thực sự, bánh ngọt tự làm không chỉ mang đến hương vị tuyệt vời mà còn chứa đựng tình yêu và sự tâm huyết trong từng công đoạn. Cảm giác tự tay làm ra những chiếc bánh ngon, rồi cùng gia đình hay bạn bè thưởng thức, quả thật là không gì sánh bằng. Tuy nhiên, mình cũng không thể không khen Ngũ Long Bakery, vì dù bánh tự làm có cái thú vị riêng, nhưng bánh từ Ngũ Long luôn đảm bảo chất lượng, hương vị hoàn hảo mỗi lần ghé!', 1, '2024-11-30 12:41:15', 1, 11),
(12, 'Bài viết này thực sự rất thú vị và bổ ích! Thưởng thức bánh không chỉ là ăn mà còn là một nghệ thuật, và những hướng dẫn trong bài giúp mình hiểu rõ hơn về cách để tận hưởng trọn vẹn hương vị từng loại bánh. Đặc biệt là cách kết hợp bánh với thức uống, hay việc thưởng thức bánh ngay sau khi ra lò để giữ nguyên độ tươi ngon – điều này quả thực giúp nâng tầm trải nghiệm ẩm thực. Ngũ Long Bakery luôn biết cách làm cho mỗi chiếc bánh trở nên đặc biệt hơn bao giờ hết!', 1, '2024-11-30 12:42:24', 1, 12),
(13, 'Bài viết rất thú vị và hấp dẫn! Ngũ Long Bakery quả thật là thiên đường của những món bánh ngon, từ bánh da lợn, bánh bò đến bánh trung thu, mỗi loại đều mang một hương vị đặc trưng không thể nhầm lẫn. Mình rất thích cách tiệm kết hợp giữa truyền thống và sáng tạo trong các món bánh, làm cho mỗi lần thưởng thức đều là một trải nghiệm mới mẻ. Nhất định phải thử hết những món này khi có dịp!', 1, '2024-11-30 12:44:11', 2, 1),
